import type React from 'react';
import {
  Info,
  Lightbulb,
  FileWarningIcon as Warning,
  XCircle,
} from 'lucide-react';
import { cn } from '@/lib/utils';

interface AdmonitionProps {
  type?: 'warning' | 'note' | 'tip' | 'info' | 'danger';
  title?: string;
  children: React.ReactNode;
  className?: string;
}

export function Admonition({
  type = 'info',
  title,
  children,
  className,
}: AdmonitionProps) {
  const typeStyles: {
    [key: string]: {
      container: string;
      icon: string;
      title: string;
      iconComponent: React.ReactNode;
    };
  } = {
    note: {
      container:
        'bg-blue-50 dark:bg-blue-950/50 border-l-4 border-blue-500 dark:border-blue-400',
      icon: 'text-blue-500 dark:text-blue-400',
      title: 'text-blue-700 dark:text-blue-300',
      iconComponent: <Info className="h-5 w-5" />,
    },
    tip: {
      container:
        'bg-green-50 dark:bg-green-950/50 border-l-4 border-green-500 dark:border-green-400',
      icon: 'text-green-500 dark:text-green-400',
      title: 'text-green-700 dark:text-green-300',
      iconComponent: <Lightbulb className="h-5 w-5" />,
    },
    info: {
      container:
        'bg-slate-50 dark:bg-slate-900/50 border-l-4 border-slate-500 dark:border-slate-400',
      icon: 'text-slate-500 dark:text-slate-400',
      title: 'text-slate-700 dark:text-slate-300',
      iconComponent: <Info className="h-5 w-5" />,
    },
    warning: {
      container:
        'bg-amber-50 dark:bg-amber-950/50 border-l-4 border-amber-500 dark:border-amber-400',
      icon: 'text-amber-500 dark:text-amber-400',
      title: 'text-amber-700 dark:text-amber-300',
      iconComponent: <Warning className="h-5 w-5" />,
    },
    danger: {
      container:
        'bg-red-50 dark:bg-red-950/50 border-l-4 border-red-500 dark:border-red-400',
      icon: 'text-red-500 dark:text-red-400',
      title: 'text-red-700 dark:text-red-300',
      iconComponent: <XCircle className="h-5 w-5" />,
    },
  };

  const defaultTitle = {
    note: 'Note',
    tip: 'Tip',
    info: 'Info',
    warning: 'Warning',
    danger: 'Danger',
  };

  const styles = typeStyles[type];
  const displayTitle = title || defaultTitle[type];

  return (
    <div
      className={cn(
        `p-4 my-6 rounded-r-md shadow-sm`,
        styles.container,
        className,
      )}
      role="alert"
    >
      <div className="flex items-start gap-3">
        <div className={cn('flex-shrink-0 mt-0.5', styles.icon)}>
          {styles.iconComponent}
        </div>
        <div className="flex-1 min-w-0">
          {displayTitle && (
            <h3 className={cn('font-medium text-base mb-1', styles.title)}>
              {displayTitle}
            </h3>
          )}
          <div className="text-sm leading-relaxed text-slate-700 dark:text-slate-300">
            {children}
          </div>
        </div>
      </div>
    </div>
  );
}

import { Admonition } from './admonition';

export default function AdmonitionDemo() {
  return (
    <div className="container mx-auto max-w-7xl px-4 py-8 space-y-8">
      <WarningDemo />
      <NoteDemo />
      <TipDemo />
      <InfoDemo />
      <DangerDemo />
    </div>
  );
}

export function WarningDemo() {
  return (
    <Admonition type="warning" title="Browser Compatibility">
      <p>
        This feature is not supported in Internet Explorer. Please use a modern
        browser for the best experience.
      </p>
    </Admonition>
  );
}

export function NoteDemo() {
  return (
    <Admonition type="note" title="Documentation Update">
      <p>
        This documentation was last updated on March 21, 2025. Some information
        may have changed since then.
      </p>
    </Admonition>
  );
}

export function TipDemo() {
  return (
    <Admonition type="tip" title="Performance Tip">
      <p>
        Use React.memo() to prevent unnecessary re-renders of expensive
        components when their props haven't changed.
      </p>
    </Admonition>
  );
}

export function InfoDemo() {
  return (
    <Admonition type="info" title="Server Components">
      <p>
        React Server Components allow you to render components on the server,
        reducing the JavaScript sent to the client.
      </p>
    </Admonition>
  );
}

export function DangerDemo() {
  return (
    <Admonition type="danger" title="Destructive Action">
      <p>
        This action will permanently delete all your data and cannot be undone.
        Make sure you have a backup before proceeding.
      </p>
    </Admonition>
  );
}
